import React, { createContext, useContext, useState, useEffect } from 'react';
import { authService } from '../services/authService';
import { userService } from '../services/userService';
import toast from 'react-hot-toast';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    initializeAuth();
  }, []);

  const initializeAuth = async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        // Try to get user profile to validate token
        const response = await userService.getProfile();
        if (response.code === '1') {
          setUser(response.data);
        } else {
          // Invalid token, clear storage
          authService.clearAuthData();
        }
      }
    } catch (error) {
      console.error('Auth initialization error:', error);
      authService.clearAuthData();
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, otp) => {
    try {
      setLoading(true);
      const response = await authService.verifyOtp(email, otp);
      
      if (response.code === '1' && response.data.token) {
        const token = response.data.token;
        authService.setAuthData(token);
        
        // Get user profile after successful login
        const profileResponse = await userService.getProfile();
        if (profileResponse.code === '1') {
          setUser(profileResponse.data);
          authService.setAuthData(token, profileResponse.data);
          toast.success('Login successful!');
          return { success: true };
        }
      }
      
      return { success: false, message: response.data || 'Login failed' };
    } catch (error) {
      console.error('Login error:', error);
      const message = error.response?.data?.data || 'Login failed. Please try again.';
      return { success: false, message };
    } finally {
      setLoading(false);
    }
  };

  const logout = async () => {
    try {
      setLoading(true);
      await authService.logout();
      setUser(null);
      toast.success('Logged out successfully');
    } catch (error) {
      console.error('Logout error:', error);
      // Clear local data even if API call fails
      authService.clearAuthData();
      setUser(null);
    } finally {
      setLoading(false);
    }
  };

  const sendOtp = async (email) => {
    try {
      const response = await authService.sendOtp(email);
      if (response.code === '1') {
        toast.success('OTP sent successfully!');
        return { success: true };
      }
      return { success: false, message: response.data || 'Failed to send OTP' };
    } catch (error) {
      console.error('Send OTP error:', error);
      const message = error.response?.data?.data || 'Failed to send OTP. Please try again.';
      return { success: false, message };
    }
  };

  const updateUserProfile = async (profileData) => {
    try {
      const response = await userService.updateProfile(profileData);
      if (response.code === '1') {
        // Refresh user data
        const profileResponse = await userService.getProfile();
        if (profileResponse.code === '1') {
          setUser(profileResponse.data);
          authService.setAuthData(localStorage.getItem('token'), profileResponse.data);
        }
        toast.success('Profile updated successfully!');
        return { success: true };
      }
      return { success: false, message: response.data || 'Failed to update profile' };
    } catch (error) {
      console.error('Update profile error:', error);
      const message = error.response?.data?.data || 'Failed to update profile. Please try again.';
      return { success: false, message };
    }
  };

  const value = {
    user,
    loading,
    login,
    logout,
    sendOtp,
    updateUserProfile,
    isAuthenticated: authService.isAuthenticated,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
