import React, { useState } from 'react';
import { toast } from 'react-hot-toast';
import { emailService } from '../../services/emailService';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import Input from '../../components/UI/Input';
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const SendEmail = () => {
  const [formData, setFormData] = useState({
    to: '',
    subject: '',
    message: '',
    name: '',
    company: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      console.log('Sending email with data:', formData); // Debug log
      
      // Prepare email data for the API
      const emailData = {
        recipientEmail: formData.to,
        subject: formData.subject,
        message: formData.message,
        // Add any additional fields that your backend expects
      };
      
      // Try different email service methods based on your backend API
      let response;
      
      // First try the intent-based email method
      try {
        response = await emailService.sendIntentEmail(emailData);
        console.log('Intent email response:', response); // Debug log
      } catch (intentError) {
        console.log('Intent email failed, trying quick send:', intentError);
        
        // If intent email fails, try quick send
        try {
          response = await emailService.quickSend(formData.to);
          console.log('Quick send response:', response); // Debug log
        } catch (quickError) {
          console.log('Quick send failed, trying bulk send:', quickError);
          
          // If quick send fails, try the bulk send method
          const hrDetailsList = [{
            email: formData.to,
            subject: formData.subject,
            message: formData.message,
            // Add other required fields for HR details if needed
            name: '', // You might want to add a name field to the form
            company: '', // You might want to add a company field to the form
          }];
          
          response = await emailService.sendEmails(hrDetailsList);
          console.log('Bulk send response:', response); // Debug log
        }
      }
      
      // Check if the response indicates success
      if (response && (response.code === '1' || response.success || response.status === 'success')) {
        toast.success('Email sent successfully!');
        setFormData({ to: '', subject: '', message: '', name: '', company: '' });
      } else {
        const errorMessage = response?.message || response?.data || 'Failed to send email';
        toast.error(errorMessage);
      }
      
    } catch (error) {
      console.error('Email send error:', error);
      const errorMessage = error.response?.data?.message || 
                          error.response?.data?.data || 
                          error.message || 
                          'Failed to send email';
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-secondary-900">Send Email</h1>
        <p className="text-secondary-600 mt-1">Compose and send a new email</p>
      </div>

      <Card>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label htmlFor="to" className="block text-sm font-medium text-secondary-700 mb-1">
              To
            </label>
            <Input
              id="to"
              name="to"
              type="email"
              value={formData.to}
              onChange={handleChange}
              placeholder="recipient@example.com"
              required
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-secondary-700 mb-1">
                Recipient Name (Optional)
              </label>
              <Input
                id="name"
                name="name"
                type="text"
                value={formData.name}
                onChange={handleChange}
                placeholder="John Doe"
              />
            </div>

            <div>
              <label htmlFor="company" className="block text-sm font-medium text-secondary-700 mb-1">
                Company (Optional)
              </label>
              <Input
                id="company"
                name="company"
                type="text"
                value={formData.company}
                onChange={handleChange}
                placeholder="Company Name"
              />
            </div>
          </div>

          <div>
            <label htmlFor="subject" className="block text-sm font-medium text-secondary-700 mb-1">
              Subject
            </label>
            <Input
              id="subject"
              name="subject"
              type="text"
              value={formData.subject}
              onChange={handleChange}
              placeholder="Email subject"
              required
            />
          </div>

          <div>
            <label htmlFor="message" className="block text-sm font-medium text-secondary-700 mb-1">
              Message
            </label>
            <textarea
              id="message"
              name="message"
              rows={8}
              value={formData.message}
              onChange={handleChange}
              placeholder="Type your message here..."
              className="input-field resize-none"
              required
            />
          </div>

          <div className="flex justify-end">
            <Button type="submit" disabled={loading}>
              {loading ? <LoadingSpinner /> : 'Send Email'}
            </Button>
          </div>
        </form>
      </Card>
    </div>
  );
};

export default SendEmail;
