  Send, 
  Users, 
  TrendingUp, 
  Clock, 
  CheckCircle,
  XCircle,
  Phone,
  Calendar,
  UserCheck
} from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { emailService } from '../../services/emailService';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';

const Dashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalEmails: 0,
    sentToday: 0,
    responseRate: 0,
    statusBreakdown: {}
  });
  const [recentEmails, setRecentEmails] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await emailService.fetchAllEmails();
      
      if (response.code === '1' && response.data) {
        const emails = response.data;
        
        // Calculate stats
        const today = new Date().toDateString();
        const sentToday = emails.filter(email => 
          email.sentAt && new Date(email.sentAt).toDateString() === today
        ).length;

        const statusBreakdown = emails.reduce((acc, email) => {
          const status = email.status || 'EMAIL_SENT';
          acc[status] = (acc[status] || 0) + 1;
          return acc;
        }, {});

        const responseCount = Object.keys(statusBreakdown).reduce((acc, status) => {
          if (status !== 'EMAIL_SENT') {
            acc += statusBreakdown[status];
          }
          return acc;
        }, 0);

        const responseRate = emails.length > 0 ? Math.round((responseCount / emails.length) * 100) : 0;

        setStats({
          totalEmails: emails.length,
          sentToday,
          responseRate,
          statusBreakdown
        });

        // Get recent emails (last 5)
        const sortedEmails = emails
          .sort((a, b) => new Date(b.sentAt) - new Date(a.sentAt))
          .slice(0, 5);
        setRecentEmails(sortedEmails);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'EMAIL_SENT':
        return <Send className="w-4 h-4 text-blue-500" />;
      case 'EMAIL_RECEIVED':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'CONTACTED_ON_PHONE':
        return <Phone className="w-4 h-4 text-purple-500" />;
      case 'Interview_Scheduled':
        return <Calendar className="w-4 h-4 text-orange-500" />;
      case 'HIRED':
        return <UserCheck className="w-4 h-4 text-green-600" />;
      case 'REJECTED':
        return <XCircle className="w-4 h-4 text-red-500" />;
      default:
        return <Mail className="w-4 h-4 text-gray-500" />;
    }
  };

  const getStatusLabel = (status) => {
    const labels = {
      'EMAIL_SENT': 'Email Sent',
      'EMAIL_RECEIVED': 'Email Received',
      'CONTACTED_ON_PHONE': 'Contacted On Phone',
      'Interview_Scheduled': 'Interview Scheduled',
      'HIRED': 'Hired',
      'REJECTED': 'Rejected'
    };
    return labels[status] || status;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-gradient-to-r from-primary-600 to-primary-700 rounded-xl p-6 text-white">
        <h1 className="text-2xl font-bold mb-2">
          Welcome back, {user?.name || 'User'}! ðŸ‘‹
        </h1>
        <p className="text-primary-100">
          Here's your email campaign overview for today.
        </p>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="bg-gradient-to-br from-blue-50 to-blue-100 border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-blue-600">Total Emails</p>
              <p className="text-2xl font-bold text-blue-900">{stats.totalEmails}</p>
            </div>
            <div className="p-3 bg-blue-500 rounded-full">
              <Mail className="w-6 h-6 text-white" />
            </div>
          </div>
        </Card>

        <Card className="bg-gradient-to-br from-green-50 to-green-100 border-green-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-green-600">Sent Today</p>
              <p className="text-2xl font-bold text-green-900">{stats.sentToday}</p>
            </div>
            <div className="p-3 bg-green-500 rounded-full">
              <Send className="w-6 h-6 text-white" />
            </div>
          </div>
        </Card>

        <Card className="bg-gradient-to-br from-purple-50 to-purple-100 border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-purple-600">Response Rate</p>
              <p className="text-2xl font-bold text-purple-900">{stats.responseRate}%</p>
            </div>
            <div className="p-3 bg-purple-500 rounded-full">
              <TrendingUp className="w-6 h-6 text-white" />
            </div>
          </div>
        </Card>

        <Card className="bg-gradient-to-br from-orange-50 to-orange-100 border-orange-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-orange-600">Active Campaigns</p>
              <p className="text-2xl font-bold text-orange-900">
                {Object.keys(stats.statusBreakdown).length}
              </p>
            </div>
            <div className="p-3 bg-orange-500 rounded-full">
              <Users className="w-6 h-6 text-white" />
            </div>
          </div>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Status Breakdown */}
        <Card>
          <Card.Header>
            <Card.Title>Email Status Breakdown</Card.Title>
          </Card.Header>
          <Card.Content>
            <div className="space-y-3">
              {Object.entries(stats.statusBreakdown).map(([status, count]) => (
                <div key={status} className="flex items-center justify-between p-3 bg-secondary-50 rounded-lg">
                  <div className="flex items-center space-x-3">
                    {getStatusIcon(status)}
                    <span className="text-sm font-medium text-secondary-700">
                      {getStatusLabel(status)}
                    </span>
                  </div>
                  <span className="text-sm font-bold text-secondary-900">{count}</span>
                </div>
              ))}
              
              {Object.keys(stats.statusBreakdown).length === 0 && (
                <div className="text-center py-8 text-secondary-500">
                  <Mail className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>No emails sent yet</p>
                  <Button 
                    className="mt-3"
                    onClick={() => navigate('/email/send')}
                  >
                    Send Your First Email
                  </Button>
                </div>
              )}
            </div>
          </Card.Content>
        </Card>

        {/* Recent Emails */}
        <Card>
          <Card.Header>
            <div className="flex items-center justify-between">
              <Card.Title>Recent Emails</Card.Title>
              <Button 
                variant="outline" 
                size="sm"
                onClick={() => navigate('/email/logs')}
              >
                View All
              </Button>
            </div>
          </Card.Header>
          <Card.Content>
            <div className="space-y-3">
              {recentEmails.map((email, index) => (
                <div key={index} className="flex items-center justify-between p-3 border border-secondary-200 rounded-lg hover:bg-secondary-50 transition-colors">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center space-x-2 mb-1">
                      {getStatusIcon(email.status)}
                      <p className="text-sm font-medium text-secondary-900 truncate">
                        {email.recipientEmail}
                      </p>
                    </div>
                    <p className="text-xs text-secondary-500">
                      {email.sentAt ? format(new Date(email.sentAt), 'MMM dd, yyyy HH:mm') : 'N/A'}
                    </p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className="text-xs px-2 py-1 bg-secondary-100 text-secondary-600 rounded-full">
                      {getStatusLabel(email.status)}
                    </span>
                  </div>
                </div>
              ))}
              
              {recentEmails.length === 0 && (
                <div className="text-center py-8 text-secondary-500">
                  <Clock className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>No recent emails</p>
                </div>
              )}
            </div>
          </Card.Content>
        </Card>
      </div>

      {/* Quick Actions */}
      <Card>
        <Card.Header>
          <Card.Title>Quick Actions</Card.Title>
        </Card.Header>
        <Card.Content>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Button 
              className="h-20 flex-col space-y-2"
              onClick={() => navigate('/email/send')}
            >
              <Send className="w-6 h-6" />
              <span>Send New Email</span>
            </Button>
            
            <Button 
              variant="secondary"
              className="h-20 flex-col space-y-2"
              onClick={() => navigate('/email/logs')}
            >
              <Mail className="w-6 h-6" />
              <span>View Email Logs</span>
            </Button>
            
            <Button 
              variant="outline"
              className="h-20 flex-col space-y-2"
              onClick={() => navigate('/intents')}
            >
              <Users className="w-6 h-6" />
              <span>Manage Intents</span>
            </Button>
          </div>
        </Card.Content>
      </Card>
    </div>
  );
};

export default Dashboard;
