import React, { useState, useEffect } from 'react';
import { emailService } from '../../services/emailService';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import { 
  Mail, 
  Calendar, 
  CheckCircle, 
  XCircle, 
  Clock, 
  AlertCircle,
  ChevronLeft,
  ChevronRight,
  Search,
  Filter,
  Download
} from 'lucide-react';

const EmailLogs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [testingConnection, setTestingConnection] = useState(false);
  
  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  
  // Filter and search state
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  const testConnection = async () => {
    setTestingConnection(true);
    try {
      console.log('Testing API connection for email logs...');
      const response = await emailService.fetchAllEmails();
      console.log('Connection test response:', response);
      if (response) {
        alert(`API Response: ${JSON.stringify(response, null, 2)}`);
      }
    } catch (error) {
      console.error('Connection test failed:', error);
      alert(`API Error: ${error.message}`);
    } finally {
      setTestingConnection(false);
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  const fetchLogs = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log('Fetching email logs...'); // Debug log
      const response = await emailService.fetchAllEmails();
      console.log('Email logs response:', response); // Debug log
      
      if (response && (response.code === '1' || response.status === '1') && response.data) {
        // Backend returns ApiResponse with code "1" and data containing the email logs
        const emailData = response.data;
        console.log('Email data:', emailData); // Debug log
        
        // Map the backend response to frontend format
        const mappedLogs = Array.isArray(emailData) ? emailData.map((log, index) => ({
          id: log.id || index + 1, // Use log.id if available, otherwise use index
          to: log.recipientEmail || log.email || 'Unknown', // Use recipientEmail from EmailLogResponse
          subject: log.subject || 'No Subject',
          status: log.status ? log.status.toLowerCase().replace('_', ' ') : 'unknown',
          timestamp: log.sentAt || new Date().toISOString(), // Use sentAt from EmailLogResponse
          name: log.name || '',
          company: log.company || '',
          mobNo: log.mobNo || ''
        })) : [];
        
        console.log('Mapped logs:', mappedLogs); // Debug log
        setLogs(mappedLogs);
      } else if (response && Array.isArray(response)) {
        // Handle case where response is directly an array
        const mappedLogs = response.map((log, index) => ({
          id: log.id || index + 1,
          to: log.recipientEmail || log.email || 'Unknown',
          subject: log.subject || 'No Subject',
          status: log.status ? log.status.toLowerCase().replace('_', ' ') : 'unknown',
          timestamp: log.sentAt || new Date().toISOString(),
          name: log.name || '',
          company: log.company || '',
          mobNo: log.mobNo || ''
        }));
        setLogs(mappedLogs);
      } else {
        console.log('No email logs found or invalid response format');
        setLogs([]);
        setError('No email logs found');
      }
    } catch (error) {
      console.error('Failed to fetch email logs:', error);
      setError(`Failed to fetch email logs: ${error.message}`);
      setLogs([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status.toLowerCase()) {
      case 'email sent':
      case 'sent':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'failed':
      case 'error':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  // Filter and paginate logs
  const filteredLogs = logs.filter(log => {
    const matchesSearch = log.to.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         log.subject.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = statusFilter === 'all' || log.status.toLowerCase().includes(statusFilter.toLowerCase());
    return matchesSearch && matchesStatus;
  });

  const totalPages = Math.ceil(filteredLogs.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedLogs = filteredLogs.slice(startIndex, startIndex + itemsPerPage);

  // Reset to first page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, statusFilter]);

  const getStatusIcon = (status) => {
    switch (status.toLowerCase()) {
      case 'email sent':
      case 'sent':
        return <CheckCircle className="w-5 h-5 text-green-500" />;
      case 'failed':
      case 'error':
        return <XCircle className="w-5 h-5 text-red-500" />;
      case 'pending':
        return <Clock className="w-5 h-5 text-yellow-500" />;
      default:
        return <AlertCircle className="w-5 h-5 text-gray-500" />;
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric', 
        year: 'numeric' 
      }),
      time: date.toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit',
        hour12: true 
      })
    };
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header Section */}
      <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-secondary-900">Email Logs</h1>
          <p className="text-secondary-600 mt-1">Track and monitor your email delivery history</p>
        </div>
        <div className="flex items-center space-x-3">
          <Button 
            variant="outline" 
            onClick={testConnection}
            disabled={testingConnection}
            className="flex items-center space-x-2"
          >
            {testingConnection ? <LoadingSpinner /> : <Download className="w-4 h-4" />}
            <span>Test API</span>
          </Button>
          <Button 
            onClick={fetchLogs} 
            disabled={loading}
            className="flex items-center space-x-2"
          >
            {loading ? <LoadingSpinner /> : <Mail className="w-4 h-4" />}
            <span>Refresh</span>
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <Mail className="w-6 h-6 text-blue-600" />
            </div>
            <div>
              <p className="text-sm text-secondary-600">Total Emails</p>
              <p className="text-2xl font-bold text-secondary-900">{logs.length}</p>
            </div>
          </div>
        </Card>
        
        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-green-100 rounded-lg">
              <CheckCircle className="w-6 h-6 text-green-600" />
            </div>
            <div>
              <p className="text-sm text-secondary-600">Sent</p>
              <p className="text-2xl font-bold text-green-600">
                {logs.filter(log => log.status.toLowerCase().includes('sent')).length}
              </p>
            </div>
          </div>
        </Card>
        
        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-red-100 rounded-lg">
              <XCircle className="w-6 h-6 text-red-600" />
            </div>
            <div>
              <p className="text-sm text-secondary-600">Failed</p>
              <p className="text-2xl font-bold text-red-600">
                {logs.filter(log => log.status.toLowerCase().includes('failed')).length}
              </p>
            </div>
          </div>
        </Card>
        
        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-yellow-100 rounded-lg">
              <Clock className="w-6 h-6 text-yellow-600" />
            </div>
            <div>
              <p className="text-sm text-secondary-600">Pending</p>
              <p className="text-2xl font-bold text-yellow-600">
                {logs.filter(log => log.status.toLowerCase().includes('pending')).length}
              </p>
            </div>
          </div>
        </Card>
      </div>

      {/* Filters and Search */}
      <Card className="p-6">
        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
          <div className="flex flex-col sm:flex-row gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search emails..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 border border-secondary-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent w-full sm:w-64"
              />
            </div>
            
            {/* Status Filter */}
            <div className="relative">
              <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400 w-4 h-4" />
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="pl-10 pr-8 py-2 border border-secondary-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent appearance-none bg-white"
              >
                <option value="all">All Status</option>
                <option value="sent">Sent</option>
                <option value="failed">Failed</option>
                <option value="pending">Pending</option>
              </select>
            </div>
          </div>
          
          <div className="text-sm text-secondary-600">
            Showing {paginatedLogs.length} of {filteredLogs.length} emails
          </div>
        </div>
      </Card>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 flex items-center space-x-3">
          <XCircle className="w-5 h-5 text-red-500 flex-shrink-0" />
          <p className="text-red-600">{error}</p>
        </div>
      )}

      {/* Email Logs Table */}
      <Card className="overflow-hidden">
        {loading && logs.length === 0 ? (
          <div className="flex flex-col items-center justify-center py-16">
            <LoadingSpinner />
            <p className="text-secondary-500 mt-4">Loading email logs...</p>
          </div>
        ) : paginatedLogs.length === 0 ? (
          <div className="text-center py-16">
            <Mail className="w-16 h-16 text-secondary-300 mx-auto mb-4" />
            <p className="text-secondary-500 text-xl font-medium">No email logs found</p>
            <p className="text-secondary-400 mt-2">
              {searchTerm || statusFilter !== 'all' 
                ? 'Try adjusting your search or filter criteria' 
                : 'Send some emails to see them here'
              }
            </p>
          </div>
        ) : (
          <>
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead className="bg-secondary-50 border-b border-secondary-200">
                  <tr>
                    <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 uppercase tracking-wider">
                      Recipient
                    </th>
                    <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 uppercase tracking-wider">
                      Subject
                    </th>
                    <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 uppercase tracking-wider">
                      Date & Time
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-secondary-100">
                  {paginatedLogs.map((log, index) => {
                    const dateTime = formatDate(log.timestamp);
                    return (
                      <tr key={log.id} className="hover:bg-secondary-25 transition-colors duration-150">
                        <td className="px-6 py-6">
                          <div className="flex items-center space-x-3">
                            <div className="p-2 bg-primary-50 rounded-full">
                              <Mail className="w-4 h-4 text-primary-600" />
                            </div>
                            <div>
                              <p className="text-sm font-medium text-secondary-900">{log.to}</p>
                              {log.name && (
                                <p className="text-xs text-secondary-500">{log.name}</p>
                              )}
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-6">
                          <div className="max-w-xs">
                            <p className="text-sm font-medium text-secondary-900 truncate">
                              {log.subject}
                            </p>
                            {log.company && (
                              <p className="text-xs text-secondary-500 mt-1">{log.company}</p>
                            )}
                          </div>
                        </td>
                        <td className="px-6 py-6">
                          <div className="flex items-center space-x-2">
                            {getStatusIcon(log.status)}
                            <span className={`inline-flex px-3 py-1 text-xs font-semibold rounded-full border ${getStatusColor(log.status)}`}>
                              {log.status}
                            </span>
                          </div>
                        </td>
                        <td className="px-6 py-6">
                          <div className="flex items-center space-x-2">
                            <Calendar className="w-4 h-4 text-secondary-400" />
                            <div>
                              <p className="text-sm font-medium text-secondary-900">{dateTime.date}</p>
                              <p className="text-xs text-secondary-500">{dateTime.time}</p>
                            </div>
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="bg-secondary-50 px-6 py-4 border-t border-secondary-200">
                <div className="flex items-center justify-between">
                  <div className="text-sm text-secondary-600">
                    Page {currentPage} of {totalPages}
                  </div>
                  
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                      disabled={currentPage === 1}
                      className="flex items-center space-x-1"
                    >
                      <ChevronLeft className="w-4 h-4" />
                      <span>Previous</span>
                    </Button>
                    
                    <div className="flex items-center space-x-1">
                      {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                        let pageNum;
                        if (totalPages <= 5) {
                          pageNum = i + 1;
                        } else if (currentPage <= 3) {
                          pageNum = i + 1;
                        } else if (currentPage >= totalPages - 2) {
                          pageNum = totalPages - 4 + i;
                        } else {
                          pageNum = currentPage - 2 + i;
                        }
                        
                        return (
                          <button
                            key={pageNum}
                            onClick={() => setCurrentPage(pageNum)}
                            className={`px-3 py-1 text-sm rounded-md transition-colors duration-150 ${
                              currentPage === pageNum
                                ? 'bg-primary-600 text-white'
                                : 'text-secondary-600 hover:bg-secondary-100'
                            }`}
                          >
                            {pageNum}
                          </button>
                        );
                      })}
                    </div>
                    
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                      disabled={currentPage === totalPages}
                      className="flex items-center space-x-1"
                    >
                      <span>Next</span>
                      <ChevronRight className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </Card>
    </div>
  );
};

export default EmailLogs;
