import React, { useState } from 'react';
import { toast } from 'react-hot-toast';
import { emailService } from '../../services/emailService';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import Input from '../../components/UI/Input';
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const QuickSend = () => {
  const [recipientEmail, setRecipientEmail] = useState('');
  const [loading, setLoading] = useState(false);

  const handleQuickSend = async (e) => {
    e.preventDefault();
    
    if (!recipientEmail) {
      toast.error('Please enter recipient email');
      return;
    }

    setLoading(true);
    
    try {
      console.log('Quick sending to:', recipientEmail); // Debug log
      
      // Try different structures for QuickSendRequest
      const quickSendData = {
        recipientEmail: recipientEmail,
        // Add other possible fields that QuickSendRequest might expect
        subject: 'Quick Send Message', // Default subject
        message: 'This is a quick send message', // Default message
        name: '', // Optional name
        company: '' // Optional company
      };
      
      console.log('Quick send data:', quickSendData); // Debug log
      
      const response = await emailService.quickSend(quickSendData);
      console.log('Quick send response:', response); // Debug log
      
      if (response && (response.code === '1' || response.status === '1' || response.success)) {
        toast.success('Quick message sent successfully!');
        setRecipientEmail('');
      } else {
        const errorMessage = response?.message || response?.data || 'Failed to send quick message';
        toast.error(errorMessage);
      }
      
    } catch (error) {
      console.error('Quick send error:', error);
      const errorMessage = error.response?.data?.message || 
                          error.response?.data?.data || 
                          error.message || 
                          'Failed to send quick message';
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-bold text-secondary-900">Quick Send</h1>
        <p className="text-secondary-600 mt-1">Send a quick message using predefined template</p>
      </div>

      <Card>
        <form onSubmit={handleQuickSend} className="space-y-4">
          <div>
            <label htmlFor="recipientEmail" className="block text-sm font-medium text-secondary-700 mb-1">
              Recipient Email
            </label>
            <Input
              id="recipientEmail"
              name="recipientEmail"
              type="email"
              value={recipientEmail}
              onChange={(e) => setRecipientEmail(e.target.value)}
              placeholder="recipient@example.com"
              required
            />
          </div>

          <div className="bg-secondary-50 p-4 rounded-lg">
            <h3 className="text-sm font-medium text-secondary-700 mb-2">Quick Send Info</h3>
            <p className="text-sm text-secondary-600">
              This will send a predefined message template to the recipient. 
              The message content is managed by the backend system.
            </p>
          </div>

          <div className="flex justify-end">
            <Button type="submit" disabled={loading}>
              {loading ? <LoadingSpinner /> : 'Send Quick Message'}
            </Button>
          </div>
        </form>
      </Card>
    </div>
  );
};

export default QuickSend;
