import React, { useState, useEffect } from 'react';
import { emailService } from '../../services/emailService';
import Card from '../../components/UI/Card';
import Button from '../../components/UI/Button';
import LoadingSpinner from '../../components/UI/LoadingSpinner';
import { 
  Mail, 
  Calendar, 
  CheckCircle, 
  XCircle, 
  Clock, 
  AlertCircle,
  ChevronLeft,
  ChevronRight,
  Search,
  Filter,
  Download
} from 'lucide-react';

const EmailLogs = () => {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [testingConnection, setTestingConnection] = useState(false);
  
  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  
  // Filter and search state
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  const testConnection = async () => {
    setTestingConnection(true);
    try {
      console.log('Testing API connection for email logs...');
      const response = await emailService.fetchAllEmails();
      console.log('Connection test response:', response);
      if (response) {
        alert(`API Response: ${JSON.stringify(response, null, 2)}`);
      }
    } catch (error) {
      console.error('Connection test failed:', error);
      alert(`API Error: ${error.message}`);
    } finally {
      setTestingConnection(false);
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  const fetchLogs = async () => {
    setLoading(true);
    setError(null);
    try {
      console.log('Fetching email logs...'); // Debug log
      const response = await emailService.fetchAllEmails();
      console.log('Email logs response:', response); // Debug log
      
      if (response && (response.code === '1' || response.status === '1') && response.data) {
        // Backend returns ApiResponse with code "1" and data containing the email logs
        const emailData = response.data;
        console.log('Email data:', emailData); // Debug log
        
        // Map the backend response to frontend format
        const mappedLogs = Array.isArray(emailData) ? emailData.map((log, index) => ({
          id: log.id || index + 1, // Use log.id if available, otherwise use index
          to: log.recipientEmail || log.email || 'Unknown', // Use recipientEmail from EmailLogResponse
          subject: log.subject || 'No Subject',
          status: log.status ? log.status.toLowerCase().replace('_', ' ') : 'unknown',
          timestamp: log.sentAt || new Date().toISOString(), // Use sentAt from EmailLogResponse
          name: log.name || '',
          company: log.company || '',
          mobNo: log.mobNo || ''
        })) : [];
        
        console.log('Mapped logs:', mappedLogs); // Debug log
        setLogs(mappedLogs);
      } else if (response && Array.isArray(response)) {
        // Handle case where response is directly an array
        const mappedLogs = response.map((log, index) => ({
          id: log.id || index + 1,
          to: log.recipientEmail || log.email || 'Unknown',
          subject: log.subject || 'No Subject',
          status: log.status ? log.status.toLowerCase().replace('_', ' ') : 'unknown',
          timestamp: log.sentAt || new Date().toISOString(),
          name: log.name || '',
          company: log.company || '',
          mobNo: log.mobNo || ''
        }));
        setLogs(mappedLogs);
      } else {
        console.log('No email logs found or invalid response format');
        setLogs([]);
        setError('No email logs found');
      }
    } catch (error) {
      console.error('Failed to fetch email logs:', error);
      setError(`Failed to fetch email logs: ${error.message}`);
      setLogs([]);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'sent':
        return 'text-green-600 bg-green-100';
      case 'failed':
        return 'text-red-600 bg-red-100';
      case 'pending':
        return 'text-yellow-600 bg-yellow-100';
      default:
        return 'text-secondary-600 bg-secondary-100';
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-secondary-900">Email Logs</h1>
          <p className="text-secondary-600 mt-1">View email delivery history and status</p>
        </div>
        <div className="flex space-x-2">
          <Button 
            variant="outline" 
            onClick={testConnection}
            disabled={testingConnection}
          >
            {testingConnection ? <LoadingSpinner /> : 'Test API'}
          </Button>
          <Button onClick={fetchLogs} disabled={loading}>
            {loading ? <LoadingSpinner /> : 'Refresh'}
          </Button>
        </div>
      </div>

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <p className="text-red-600">{error}</p>
        </div>
      )}

      <Card>
        {loading && logs.length === 0 ? (
          <div className="flex justify-center items-center h-64">
            <LoadingSpinner />
          </div>
        ) : logs.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-secondary-500 text-lg">No email logs found</p>
            <p className="text-secondary-400 mt-2">Send some emails to see them here</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-secondary-200">
              <thead className="bg-secondary-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">
                    Recipient
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">
                    Subject
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-secondary-500 uppercase tracking-wider">
                    Timestamp
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-secondary-200">
                {logs.map((log) => (
                  <tr key={log.id}>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-secondary-900">
                      {log.to}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-secondary-900">
                      {log.subject}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(log.status)}`}>
                        {log.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-secondary-500">
                      {new Date(log.timestamp).toLocaleString()}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>
    </div>
  );
};

export default EmailLogs;
