import React, { useState } from 'react';
import { Plane, Mail, Shield } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import Button from '../../components/UI/Button';
import Input from '../../components/UI/Input';
import Card from '../../components/UI/Card';
import toast from 'react-hot-toast';

const Login = () => {
  const [step, setStep] = useState('email'); // 'email' or 'otp'
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const { login, sendOtp } = useAuth();

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleSendOtp = async (e) => {
    e.preventDefault();
    setErrors({});

    if (!email) {
      setErrors({ email: 'Email is required' });
      return;
    }

    if (!validateEmail(email)) {
      setErrors({ email: 'Please enter a valid email address' });
      return;
    }

    setLoading(true);
    try {
      console.log('Sending OTP to:', email); // Debug log
      const result = await sendOtp(email);
      console.log('SendOTP result:', result); // Debug log
      setLoading(false);

      if (result.success) {
        console.log('OTP sent successfully, changing step to otp'); // Debug log
        setStep('otp');
        // Don't show toast here since it's already shown in sendOtp
      } else {
        console.log('OTP send failed:', result.message); // Debug log
        setErrors({ email: result.message });
      }
    } catch (error) {
      console.error('HandleSendOtp error:', error); // Debug log
      setLoading(false);
      setErrors({ email: 'Failed to send OTP. Please try again.' });
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setErrors({});

    if (!otp) {
      setErrors({ otp: 'OTP is required' });
      return;
    }

    if (otp.length !== 6) {
      setErrors({ otp: 'OTP must be 6 digits' });
      return;
    }

    setLoading(true);
    const result = await login(email, otp);
    setLoading(false);

    if (!result.success) {
      setErrors({ otp: result.message });
    }
  };

  const handleResendOtp = async () => {
    setLoading(true);
    const result = await sendOtp(email);
    setLoading(false);

    if (!result.success) {
      toast.error(result.message);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-50 to-secondary-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Logo and Title */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-600 rounded-2xl mb-4">
            <Plane className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-secondary-900 mb-2">Mail Pilot</h1>
          <p className="text-secondary-600">Professional Email Management System</p>
        </div>

        <Card className="fade-in">
          {step === 'email' ? (
            <>
              <Card.Header>
                <Card.Title className="text-center">Sign In</Card.Title>
                <p className="text-sm text-secondary-600 text-center mt-2">
                  Enter your email to receive a verification code
                </p>
              </Card.Header>

              <Card.Content>
                <form onSubmit={handleSendOtp} className="space-y-4">
                  <Input
                    type="email"
                    label="Email Address"
                    placeholder="Enter your email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    error={errors.email}
                    required
                    icon={<Mail className="w-5 h-5" />}
                  />

                  <Button
                    type="submit"
                    className="w-full"
                    loading={loading}
                  >
                    Send Verification Code
                  </Button>
                </form>

                <div className="mt-6 text-center">
                  <div className="flex items-center justify-center space-x-2 text-sm text-secondary-500">
                    <Shield className="w-4 h-4" />
                    <span>Secure OTP-based authentication</span>
                  </div>
                </div>
              </Card.Content>
            </>
          ) : (
            <>
              <Card.Header>
                <Card.Title className="text-center">Verify Your Email</Card.Title>
                <p className="text-sm text-secondary-600 text-center mt-2">
                  We've sent a 6-digit code to <strong>{email}</strong>
                </p>
              </Card.Header>

              <Card.Content>
                <form onSubmit={handleVerifyOtp} className="space-y-4">
                  <Input
                    type="text"
                    label="Verification Code"
                    placeholder="Enter 6-digit code"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                    error={errors.otp}
                    required
                    maxLength={6}
                    className="text-center text-lg tracking-widest"
                  />

                  <Button
                    type="submit"
                    className="w-full"
                    loading={loading}
                  >
                    Verify & Sign In
                  </Button>
                </form>

                <div className="mt-6 text-center space-y-3">
                  <button
                    type="button"
                    onClick={handleResendOtp}
                    className="text-sm text-primary-600 hover:text-primary-700 font-medium"
                    disabled={loading}
                  >
                    Resend Code
                  </button>

                  <div>
                    <button
                      type="button"
                      onClick={() => {
                        setStep('email');
                        setOtp('');
                        setErrors({});
                      }}
                      className="text-sm text-secondary-500 hover:text-secondary-700"
                    >
                      ← Back to email
                    </button>
                  </div>
                </div>
              </Card.Content>
            </>
          )}
        </Card>

        {/* Features */}
        <div className="mt-8 grid grid-cols-1 gap-4 text-center">
          <div className="bg-white/50 backdrop-blur-sm rounded-lg p-4">
            <h3 className="font-semibold text-secondary-900 mb-2">Features</h3>
            <ul className="text-sm text-secondary-600 space-y-1">
              <li>• Bulk email sending with attachments</li>
              <li>• Email status tracking and analytics</li>
              <li>• Intent-based email templates</li>
              <li>• Professional resume management</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
